column19:
if {[IsPresent]} {
  if {![CWL "1,2"]} {setError "Entity Type Qualifier Must Be 1 Or 2" }
}

if { ![IsPresent] } {
   setError "Entity Type Qualifier Must Be Present" 
} else {
   if { ![CWL "1,2"] } { setError "Entity Type Qualifier Must Be 1 Or 2" }
}

if { ![IsPresent] } {
   setError "Submitter Name Must Be Present" 
} else {
   if { [ValidCharSet "$BIG_CHARS_LIST"] != 1 } {
      setError "Submitter Name Contains Invalid Characters" 
      return
   }
   if { [string length $thisvalue] > 60 } {
      setError "Submitter Name Cannot Exceed 60 Characters" 
   }
}

if { [IsPresent] } {
   if { [getField CLAIM.SUBM_TYPE_QUALIFIER] == "2" } {
      setError "Submitter First Name Cannot Be Present When Qualifier = 2" 
      return
   }
   if { [ValidCharSet "$BIG_CHARS_LIST"] != 1 } {
      setError "Submitter First Name Contains Invalid Characters" 
      return
   }
   if { [string length $thisvalue] > 35 } {
         setError "Submitter First Name Cannot Exceed 35 Characters" 
   }
}

if {[IsPresent]} {
   if { [getField CLAIM.SUBM_TYPE_QUALIFIER] == "2" } {
      setError "Submitter Middle Name Cannot Be Present When Qualifier = 2" 
      return
   }
   if { [ValidCharSet "$VALID_NAME_CHARS_SHT"] != 1 } {
      setError "Submitter Middle Name Contains Invalid Characters" 
      return
   }
   if { [string length $thisvalue] > 25 } {
      setError "Submitter Middle Name Cannot Exceed 25 Characters" 
   }
   if { $vScript=="MCAR." || $vScript=="MPA." || $vScript=="MCBRR." } {
       set first [string range $thisvalue 0 0]
     if {[IsText $first]!=1} {   
       setError "Submitter Middle Name Must Start With An Alpha Character."
     }
   }
}

set key ""
set num ""
set num1 ""

set recid [getField CLAIM.RECEIVER_ID_CODE]
if {![IsPresent] || $thisvalue == "-"} { 
   set key ""
   setError "Submitter ID Number Is Missing"
   return
} else {
   set key [string range $thisvalue 0 0]
   if {![IsAlphaNum $thisvalue]} {
      setError "Submitter ID must be Alphanumeric"
      return
   } else { 
      if { [string length $thisvalue] > 80 } {
         setError "Submitter ID Cannot Exceed 80 Characters" 
         return
      }
   }
}
# num is a list of allowed receiver ids
switch $key {
  "C" { set num1 "00520"; set num "07102,04312,07202,07302,04112,04212,04412" }
  "E" { set num1 "00520"; set num "07102" }
  "L" { set num "00528" }
  default {
    setError "First Position Must Be A Specific Character \[$thisvalue\] is an invalid SUBID"
    return
  }
}
if { ![IsPresent $recid] } { 
 setError "Receiver Organization ID required" CLAIM.RECEIVER_ID_CODE
 return
} else {
# the way we handle this will need changed once we have something other than ok mc and mcbrr.
  if { ([string equal $num1 $recid]==0 && [CWL $num $recid]!=1 ) && $key != "" } { 
# we don't know which number is required for C submitters, could be rr, or ok mc at this time
# eventually will be more, so msg must be generic.
     if { $key=="C" } {
       setError "Submitter ID and Receiver Org ID combination invalid" CLAIM.RECEIVER_ID_CODE
       return
     }
     setError "Submitter ID and Receiver Org ID combination invalid \[$thisvalue\] requires \[$num\]" CLAIM.RECEIVER_ID_CODE
     return
   }
}

if {[IsPresent]} {
   set first [string range $thisvalue 0 0]
   if {[CWL "X" $first]==1} {
       if {[string length $thisvalue]!=5} {
       setError "Submitter ID Must Have Length of 5"
       return
       }
   return
   }
   set payto [getField PAYTOPROVIDER]
   if {[CWL "E4798"] && ($payto != "1124493523" && $payto != "1326340944")} {
      setError "Invalid Billing NPI for Submitter E4798" BILLINGPROVIDER
   } 
   if {[CWL "E5905"] && ($payto == "1124493523" || $payto == "1326340944")} {
      setError "Invalid Billing NPI for Submitter E5905" BILLINGPROVIDER
   }  
   if {[CWL "E2657"] && ($payto != "1295113959")} {
      setError "Invalid Billing NPI for Submitter E2657" BILLINGPROVIDER
   }
   if {[CWL "E,U" $first]==1 || ([isCOMMERCIAL]==1 && [CWL "C" $first]==1)} {
       if {[string length $thisvalue]!=5} {
       setError "Submitter ID Must Have Length of 5"
       }
   } else {
       setError "Invalid Submitter Number For This Payer"
     }
}

if {[IsPresent] } {
      setError "Secondary Contact Name Is Not Allowed"
}

if { [IsPresent $thisvalue] != 1 } {
   setError "Receiver Name must be present" 
   return
}
if { [ValidCharSet "$BIG_CHARS_LIST"] != 1 } {
   setError "Receiver Name Contains Invalid Characters" 
   return
} 
if { [string length $thisvalue] > 60 } {
   setError "Receiver Name Cannot Exceed 60 Characters" 
}

set toDate [getField CLAIM.STMTTO]
set fromDate [getField CLAIM.STMTFR]

if {[IsPresent]} {
   if { [IsValidCode TAXOMY $thisvalue] != 1 } {
      setError "Invalid Taxonomy Code"
      return
   } 
   if {[IsPresent $fromDate]} {
      if {[IsPresent $toDate]} {
         if {[IsValidCode TAXOMY $thisvalue $fromDate $toDate]!=1} {
            setError "Taxonomy Code Not Valid For Statement Date"
             return
         }     
      } else {
         if {[IsValidCode TAXOMY $thisvalue $fromDate]!=1} {
            setError "Taxonomy Code Not Valid For Statement Date"
            return
         }         
      }
   }   
} else {
   if {[IsPresent [getField PROVIDER_CODE]]} {
      setError "Taxonomy Code is Required"
   }
}

if {[IsPresent]} {
  if {![CWL "1,2"]} {setError "Entity Type Qualifier Must Be 1 Or 2" }
}

#requires edit 0 be run to set some initial global variables.
#if no ref seg info and lob then exit the edit.
set sub_id [getField CLAIM.SUBEIN]
set pyr_key [getField CLAIM.PAYER_KEY]
if { $sub_id == "X0020" } {
  if { $pyr_key == "ACCESSONLY" } { return }
  set verified [verify2010AA_5]
} else {
  set verified [verify2010AA_5]
}
set bill [getField BILLING_LN]
if { [IsPresent $bill]} {
   if { ![ValidCharSet "$BIG_CHARS_LIST" $bill] } { setError "Last Name/Organization Name Contains Invalid Character(s)" }
}

if {[IsPresent]!=1} {
   setError "Billing Provider Name Is A Required Field." 
   return
}
set len [string length $thisvalue]
if { ![ValidCharSet "$BIG_CHARS_LIST"] } { 
  setError "Billing Provider Name Cannot Contain Invalid Character(s)"
  return
 }
if {($len>60)} {setError "Length of Billing Provider Name Must Not Exceed 60 Characters."}

set len [string length $thisvalue]
if {[IsPresent]} {
  if { ![ValidCharSet "$BIG_CHARS_LIST"] } { 
    setError "Billing Provider First Name Cannot Contain Invalid Character(s)"
    return
   } 
  if {($len>35)} {setError "Length of Billing Provider First Name Must Not Exceed 35 Characters."}
}

set BillProv [getField BILLINGPROVTYPE]
if {($BillProv=="2")} {
  if {[IsPresent]} {
    setError "Billing Provider Middle Name Must Not Be Present If This Is A Non-Person Name."
    return
  }
}
if {[IsPresent]} {
  if {![ValidCharSet $BIG_CHARS_LIST]} {
    setError "Billing Provider Middle Name Contains Invalid Characters."
    return
  }
  set len [string length $thisvalue]
  if {($len>25)} {setError "Billing Provider Middle Name Cannot Exceed 25 Characters." }
  if { $vScript=="MCAR." || $vScript=="MPA." || $vScript=="MCBRR." } {
   set first [string range $thisvalue 0 0]
   if {[IsText $first]!=1} {
    setError "Billing Provider Middle Name Must Start With An Alpha Character."
   }
 }
}

set BillProv [getField BILLINGPROVTYPE]
if {($BillProv=="2")} {
  if {[IsPresent]} {
    setError "Billing Provider Suffix Must Not Be Present If This Is A Non-Person Name."
    return
  }
}
if {[IsPresent]} {
  if {![ValidCharSet #$BIG_CHARS_LIST]} {
    setError "Billing Provider Suffix Contains Invalid Characters."
    return
  }
  set len [string length $thisvalue]
  if {($len>10)} {setError "Billing Provider Suffix Cannot Exceed 10 Characters." }
}

set optout [getField PAYTO_OPTOUT]
if {$optout == "Y"} {
   setError "Provider has opted-out of Medicare. Provider Cannot File to Medipak Advantage."
}

if {[IsPresent [getField CLAIM.STMTTO]]} {
   set svc_to [getField CLAIM.STMTTO]
   if {[CompDateFields ">" "12/31/2015" $svc_to]} {
      #want to return here, pass to mpa
      return
   } else {
      setError "Medi-Pak Advantage is no longer accepting 2015 claims. This change went into effect on 3/31/2017. We apologize for the inconvenience" SVCFROM
      set terminate 1
   }
} else {
   if {[IsPresent [getField SVCDATE]]} {
      set svc_fr [getField SVCDATE]
   } else {
      set svc_fr [getField CLAIM.STMTFR]
   }
   if {[CompDateFields "<" "01/01/2016" $svc_fr]} {
      setError "Medi-Pak Advantage is no longer accepting 2015 claims. This change went into effect on 3/31/2017. We apologize for the inconvenience" SVCFROM
      set terminate 1
   }
}

set typecd [getField CLAIM.PAYTO_TYPE_CD]
set trmcd [string trim $typecd]
set paytotype [getField CLAIM.PAYTO_PRVTYPE]
set payorg [getField CLAIM.PYR_ORG_ID]
set submitid [getField CLAIM.SUBEIN]

if {($payorg=="011B63")||($payorg=="000002")||($payorg=="0119C7")||($payorg=="0119C8")} {
   if {($submitid=="X0020")} {
      return
   }
}
if {$paytotype=="F"} {
   if {$trmcd=="H"|| $trmcd=="A" || $trmcd=="T"} {
      setError "Professional Services Cannot be Submitted with a Hospital/Facility Pay To/Billing Provider NPI"
      return
    }
}

set spec [getField PAYTO_SPEC01]
if {($spec=="0FA")} {
   setError "The Provider Specialty is Not Accepted by this Payer"
}

set specialty [getField PAYTO_SPEC01]
if {[CWL "0PC,0PE" $specialty]} {
   setError "Licensed Professional Counselors and Licensed Professional Examiners Cannot File to Medipak Advantage."
}

if {![IsPresent]} { 
   setError "Billing Provider Address Is Required"
   return
} 
if { [ValidCharSet "$BIG_CHARS_LIST"]!=1 } { setError "Billing Provider Address Contains Invalid Character(s)" }
set len [string length $thisvalue]
if { ($len>55) } { setError "Billing Provider Address Cannot Contain More Than 55 Characters" }
if { ( [string equal -length 15 "POST OFFICE BOX" $thisvalue] == 1 ) || ( [string equal -length 8 "P.O. BOX" $thisvalue] == 1 ) || ( [string equal -length 6 "PO BOX" $thisvalue] == 1) || ( [string equal -length  8 "LOCK BOX" $thisvalue] == 1 ) || ( [string equal -length  8 "LOCK BIN" $thisvalue] == 1 ) || ( [string equal -length 7 "P O BOX" $thisvalue] == 1 ) || ( [string equal -length 5 "POBOX" $thisvalue] == 1 ) || ( [string equal -length 7 "P.O.BOX" $thisvalue] == 1 ) || ( [string equal -length 13 "POSTOFFICEBOX" $thisvalue] == 1 ) || ( [string equal -length 3 "BOX" $thisvalue] == 1 ) || ( [string equal -length 4 "PBOX" $thisvalue] == 1 )} { setError "Street Address Required for Billing Provider Address" }

if {[IsPresent]} {
   set len [string length $thisvalue]
   if { [ValidCharSet "$BIG_CHARS_LIST"]!=1 } { setError "Billing Provider Second Address Line Contains Invalid Character(s)" }

   if { ( [string equal -length 15 "POST OFFICE BOX" $thisvalue] == 1 ) || ( [string equal -length 8 "P.O. BOX" $thisvalue] == 1 ) || ( [string equal -length 6 "PO BOX" $thisvalue] == 1) || ( [string equal -length  8 "LOCK BOX" $thisvalue] == 1 ) || ( [string equal -length  8 "LOCK BIN" $thisvalue] == 1 ) || ( [string equal -length 7 "P O BOX" $thisvalue] == 1 ) || ( [string equal -length 5 "POBOX" $thisvalue] == 1 ) || ( [string equal -length 7 "P.O.BOX" $thisvalue] == 1 ) || ( [string equal -length 13 "POSTOFFICEBOX" $thisvalue] == 1 ) || ( [string equal -length 3 "BOX" $thisvalue] == 1 ) || ( [string equal -length 4 "PBOX" $thisvalue] == 1 )} { 
   setError "Street Address Required for Billing Provider Address" 
   }
  if { ($len>55) } { setError "The length of the Billing Provider Address Cannot exceed 55 Characters." }
}

if { [IsPresent] == 1 } {
   if { [ValidCharSet "$BIG_CHARS_LIST"] != 1 } { setError "Billing Provider Service City Contains Invalid Character(s)" }
   if { ($vScript=="MCAR." || $vScript=="MPA.") || $vScript=="MCBRR." } {
     set len [string length $thisvalue]
     if { ( $len < 2 ) || ( $len > 30 ) } {
      setError "Billing Provider Service City Cannot Be More Than 30 Characters Or Less Than 2 Characters In Length."
     }
   }
} else {
   setError "Billing Provider Service City Is A Required Field."
}

if {[IsPresent]!=1} {
   setError "Billing Provider State Is A Required Field."
   return
}
if { [ValidState $thisvalue]!=1 } { setError "Billing Provider State Must Be A Valid State." }

if {[IsPresent]!=1} { 
  setError "The Billing Provider Zip Code Is A Required Field"
  return
}
if { [ValidCharSet "0-9"] != 1 } {
   setError "Billing Provider Zip Code Contains Invalid Character(s)"
   return
}
if {[string length $thisvalue]!=9} {
   setError "Billing Provider Zip Code Must Be 9 Digits"
   return
}
if { [IsCorrectZip SVCST] != 1 } {
   setError "Billing Provider Zip Code Contains An Invalid Zip Code For The State Provided"
}

if {[IsPresent]!=1} {
   setError "Billing Provider Tax ID Type Is A Required Field."
   return
}
if {![CWL "EI,SY"]} {setError "Provider ID Qualifier Must be EI, SY"}

set subID [getField SUBEIN]
if {$subID=="X0020" && $vScript!="MPA."} {
  return
}

if {[IsPresent]} {
   if {![CWL "0B,1G"]} { 
      setError "Provider ID Qualifier Must be 0B or 1G"
   }
}

set iterateALL 1

if {[IsPresent]} { 
  if {$thisfldno==2} {
    setError "Only First Contact Name Allowed."
    return
  }

  if {([IsPresent [getField PROV_CNQ1:]]!=1) } {
    setError "Contact Qual Required When Contact Name Is Present."
    return
  }
  if {![ValidCharSet $BIG_CHARS_LIST]} {
    setError "Provider Contact Name Contains invalid Characters."
    return
  }
  set len [string length $thisvalue]
  if {($len>60)} {
    setError "Contact Name Cannot Be Longer Than 60 Characters."
    return 
  }
  set SubContact [getField SUBM_CONTACT]
  set comp [string compare $thisvalue $SubContact]
  if { ($comp==0) } {
    setError "Provider Contact Name Cannot Be The Same As The Submitter Contact Name."
    return
  }
}

set Num [getField PROV_CN1:1]

if {$thisfldno==1} {
  if {[IsPresent [getField PROVIDER_CONTACT_NAME:]]} {
    if {([IsPresent] && [IsPresent $Num]!=1)} { 
      setError "Contact Number Is Required When The Contact Qualifier Is Present."
      return
    } else {   
      if { ([IsPresent] && ![CWL "EM,TE,FX"])} {
        setError "Provider Contact Qualifier Must Contain EM,FX, or TE."
        return
      }
    }
  }
}

set qual [getField CLAIM.PROV_CNQ1:$thisfldno]
#NOTE: if the qual or this field is not present, edit 1765 will fire.
if {[IsPresent]==1} {
  set len [string length $thisvalue]
  if {($qual=="TE" || $qual=="FX") && (![IsNumeric] || $len!=10)} { setError "10 Digits Required" }
  if {($len>256)} {setError "Max length is 256 characters."}
}

set Num [getField PROV_CN2:1]

if {([IsPresent] && [IsPresent $Num]!=1)} { 
  setError "Contact Number Is Required When The Contact Qualifier Is Present."
  return
}   
if {([IsPresent]!=1 && [IsPresent $Num])} {
  setError "Contact Number Qual Is Required When The Contact Is Present."
  return
}   

if {[IsPresent]} {
  if {[CWL "TE,EX,EM,FX"]!=1} {
    setError "Contact Qualifier Must Be TE, FX, EM, or EX"
    return
  }          
  if {[CWL "EX"]} {
    set qual1 [getField PROV_CNQ1:1]
    if {![CWL "TE" $qual1]} {setError "Previous Contact Number Must Be A Telephone Number For This Field To Be An Extension"}
  }
}

set qual [getField CLAIM.PROV_CNQ2:$thisfldno]
set len [string length $thisvalue]
if {[IsPresent]==1} {
  if {[IsPresent $qual]==0} { setError "qualifier missing" CLAIM.PROV_CNQ2:$thisfldno }
  if {($len>256)} {setError "Contact Number cannot be greater in length than 256 Characters."}
  if {($qual=="TE" || $qual=="FX") && (![IsNumeric] || [string length $thisvalue]!=10)} { setError "10 digits required" }
  if {($qual=="EX") && ![IsAlphaNum]} { setError "AlphaNumeric data required" }
} elseif {[IsPresent $qual]==1} {
  setError "missing value when qualifier present"
}

set Num [getField PROV_CN3:1]

if {([IsPresent] && [IsPresent $Num]!=1)} { 
  setError "Contact Number Is Required When The Contact Qualifier Is Present."
  return
}   
if {([IsPresent]!=1 && [IsPresent $Num])} {
  setError "Contact Number Qual Is Required When The Contact Is Present."
  return
}   

if {[IsPresent]} {
  if {[CWL "TE,EX,EM,FX"]!=1} {
    setError "Contact Qualifier Must Be TE, FX, EM, or EX"
    return
  }          
  if {[CWL "EX"]} {
    set qual1 [getField PROV_CNQ2:1]
    if {![CWL "TE" $qual1]} {setError "Previous Contact Number Must Be A Telephone Number For This Field To Be An Extension"}
  }
}

#NOTE: Presence of contact number is done in edit 3059
set qual [getField CLAIM.PROV_CNQ3:$thisfldno]
if {[IsPresent]==1} {
  if {($qual=="TE" || $qual=="FX") && (![IsNumeric] || [string length $thisvalue]!=10)} { 
    setError "10 digits required" 
    return
  }
  if {($qual=="EX") && ![IsAlphaNum]} { 
    setError "AlphaNumeric data required"
    return
  }
  set len [string length $thisvalue]
  if {($len>256)} {setError "Contact Number Cannot Exceed 256 Characters in Length."}
}

if { [IsPresent $thisvalue] == 1 } {
   if { ![CWL "1,2"] } {
      setError "Pay To Provider Entity Type Must be 1 or 2"
      return
   }
}

set len [string length $thisvalue]
if { [isPresent2010AB_5] } {
  if { ![IsPresent] } {
    setError "Pay To Address Line 1 Required When Pay To Loop Present"
    return
  } else {
    if {![ValidCharSet $BIG_CHARS_LIST]} {
       setError "Pay To Address Contains Invalid Characters"
       return
    }
    if { $len > 55 } {
       setError "Pay To Address Invalid Length"
       return
    }
  }
  if { [is2010AAEqual2010AB] && [getField "CLAIM.PYRNUM"] == "PASSE" } {
     setError "Address in 2010AB should not be sent when the address in the same as in 2010AA"
  }
}

set len [string length $thisvalue]
if { [IsPresent] } {
  if {![ValidCharSet $BIG_CHARS_LIST]} {
    setError "Pay To Address Contains Invalid Characters"
    return
  }
  if { $len > 55 } {
    setError "Pay To Address Invalid Length"
    return
  }
}

set len [string length $thisvalue]
if { [isPresent2010AB_5] } {
  if { ![IsPresent] } {
    setError "Pay To City Required When Pay To Loop Present"
    return
  } else {
    if {![ValidCharSet $BIG_CHARS_LIST]} {
       setError "Pay To City Contains Invalid Characters"
       return
    }
    if { ($len < 2) || ($len > 55) } {
       setError "Pay To Address Invalid Length"
       return
    }
  }
}

if { ([isPresent2010AB_5]) && ([IsPresent]!=1)} {
  setError "Pay To Provider State Field Is Required."
  return
}
if { [getField "CLAIM.PYRNUM"] == "PASSE" } {
   if { [getField PAYTO_COUNTRY] == "US" } {
      if { [ValidState] == 1 } {
         setError "Value US is only used when services are rendered outside of the United States." PAYTO_COUNTRY
      }
   }
}
if { [IsPresent]==1 } {
   if { [ValidState] != 1 } { setError "Pay To Provider State Invalid" }
   return
}

if { [isPresent2010AB_5] == 1 } {
   if { [IsPresent] == 1 } {
      if { [ValidCharSet "A-Z,0-9"] != 1 } { setError "Contains Invalid Character(s)" }
      if { [IsCorrectZip PAYST] != 1 } { setError "Invalid Zip Code" }
   } else {
     setError "Pay-To Zip is Required"
     return
   }
}

if { [IsPresent] != 1 } { 
   setError "Payer Responsibility Sequence Code required" 
   return
} 
if { [CWL "A,B,P,S,T"] != 1 } { 
   setError "Payer Responsibility Sequence Code must = (A,B,P,S,T)" 
   return
}

if { ($vScript=="MCAR." || $vScript=="MCBRR.") && ($thisvalue=="S") } {
  set amt1 [IsPresentZero [getField "CLAIM.PAYER\[SEQNUM=2\].837.PAYER.PYRAMTPD"]]
  set amt2 [IsPresentZero [getField "CLAIM.PAYER\[SEQNUM=3\].837.PAYER.PYRAMTPD"]]
  set amt3 [IsPresentZero [getField "CLAIM.PAYER\[SEQNUM=4\].837.PAYER.PYRAMTPD"]]
  set amt4 [IsPresentZero [getField "CLAIM.PAYER\[SEQNUM=5\].837.PAYER.PYRAMTPD"]]
  set amt5 [IsPresentZero [getField "CLAIM.PAYER\[SEQNUM=6\].837.PAYER.PYRAMTPD"]]
  set amt6 [IsPresentZero [getField "CLAIM.PAYER\[SEQNUM=7\].837.PAYER.PYRAMTPD"]]
  set amt7 [IsPresentZero [getField "CLAIM.PAYER\[SEQNUM=8\].837.PAYER.PYRAMTPD"]]
  set amt8 [IsPresentZero [getField "CLAIM.PAYER\[SEQNUM=9\].837.PAYER.PYRAMTPD"]]
  set amt9 [IsPresentZero [getField "CLAIM.PAYER\[SEQNUM=10\].837.PAYER.PYRAMTPD"]]
  set amt10 [IsPresentZero [getField "CLAIM.PAYER\[SEQNUM=11\].837.PAYER.PYRAMTPD"]]

  set count [expr $amt1 + $amt2 + $amt3 + $amt4 + $amt5 + $amt6 + $amt7 + $amt8 + $amt9 + $amt10]

  if {$count==0} {
    setError "Payer Paid Amount Must Be Present When Medicare Is Secondary." "CLAIM.PAYER\[SEQNUM=2\].837.PAYER.PYRAMTPD"
    return
   }
  if {$count>1} {
    setError "Only One Payer Amount Paid Is Allowed When Medicare Is Secondary." "CLAIM.PAYER\[SEQNUM=2\].837.PAYER.PYRAMTPD"
    return
  }
}


if {[IsPresent]} {
   if { [CWL "P,S"] != 1} {
      setError "Payer Responsibility Sequence Code must = (P or S)"
      return
   } else {
      if { [CWL "S"] == 1} {
        set rc [checkSBR_5]
      }
   }
} else {
   setError "Subscriber Responsibility Sequence Code is required"
   return
}

if {![CWL "X0020,E0386,E0450,E0161,E0727,E0728,E2182,E4454,E6014,E5894,E1842" [getField SUBEIN]]} {
   if {![CWL "P"]} {
      set xover [checkDupXover]
   }
}

if {$thisvalue==[getField "CLAIM.PAYER\[SEQNUM=2\].837.PAYER.PAYER_SEQUENCE_NO"]} {
   setError "2000B Payer Responsibility Cannot Be The Same As 2320 Other Payer Responsibility"
   setError "2000B Payer Responsibility Cannot Be The Same As 2320 Other Payer Responsibility" "CLAIM.PAYER\[SEQNUM=2\].837.PAYER.PAYER_SEQUENCE_NO"
}

if { [IsPresent] != 1 } { 
   setError "Patient Relationship Required" 
   return
} 
if { $thisvalue != 18 } { 
   setError "Patient Relationship Field Must Equal 18 (Patient is Self)" 
   return
}

set grpnm [getField INS_GRPNAME]
if { [IsPresent $grpnm] } {
