    set cpt92250 $thisdetno
  }
}

if {$thisdetno==$maxdetno} {
  if {![CWL "1" $cpt2022f] && ![CWL "0" $cpt92250]} {
     setError "CPT 92250 requires an associated CPT II code 2022F" DETAIL.$cpt92250.FA0_HCPCS
  }
}

